export default interface IStorageUtility {
    get(key: string, options?: {
        errorIfNull?: boolean;
        secure?: boolean;
    }): Promise<string | undefined>;
    set(key: string, value: string, options?: {
        secure?: boolean;
    }): Promise<void>;
    delete(key: string, options?: {
        secure?: boolean;
    }): Promise<void>;
    getForUser(userId: string, key: string, options?: {
        errorIfNull?: boolean;
        secure?: boolean;
    }): Promise<string | undefined>;
    setForUser(userId: string, values: Record<string, string>, options?: {
        secure?: boolean;
    }): Promise<void>;
    deleteForUser(userId: string, key: string, options?: {
        secure?: boolean;
    }): Promise<void>;
    deleteAllUserData(userId: string, options?: {
        secure?: boolean;
    }): Promise<void>;
    storeResourceServerSessionInfo(webId: string, resourceServerIri: string, sessionExpires: number): Promise<void>;
    clearResourceServerSessionInfo(resourceServerIri: string): Promise<void>;
    safeGet(key: string, options?: Partial<{
        schema?: Record<string, any>;
        postProcess?: (retrievedObject: any) => any;
        userId?: string;
        secure?: boolean;
    }>): Promise<any | undefined>;
}
